import React from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';


class Header extends React.Component {

    state = {
        login: false
    }

    shouldComponentUpdate(nextProps, NextState) {
        let data = JSON.parse(sessionStorage.getItem("login"))
        if (data) {
            this.setState({ NextState: true })
            return true;
        } else {
            this.setState({ NextState: this.state.login })
        }

    }

    // componentDidMount() {

    //     try {
    //         const getdata = JSON.parse(sessionStorage.getItem("login"));
    //         console.log(getdata)
    //         if (getdata) {
    //             this.setState({ login: true });
    //         }
    //     } catch (e) {
    //         console.log(e)
    //     }
    // }
    // componentDidUpdate(prevProps, prevState) {
    //     console.log(prevState);
    //     console.log(`rabin ${this.state.login}`)

    //     this.forceUpdate(this.componentDidMount)
    //     if (prevState.login !== this.state.login) {
    //         this.forceUpdate(this.componentDidMount)
    //         // console.log('shyam')
    //     }
    // }

    // https://evemoo.slack.com/z-app-154452065571-509788373719-64474eb583fcd04e0e5c5a6803edbcb786dc409297c5ca5a88a34bf9192e5aea
    render() {
        return (
            <Navbar inverse collapseOnSelect>
                <Navbar.Header>
                    <Navbar.Brand>
                        <NavLink to="/" >Home</NavLink>
                    </Navbar.Brand>
                    <Navbar.Toggle />
                </Navbar.Header>
                <Navbar.Collapse>
                    <Nav>
                        <LinkContainer to="/shop">
                            <NavItem eventKey={1}>Shop</NavItem>
                        </LinkContainer>
                    </Nav>
                    <Nav pullRight>
                        {this.state.login ?
                            <LinkContainer to="/addtocart">
                                <NavItem eventKey={1}>Add To cart</NavItem>
                            </LinkContainer>
                            :
                            <LinkContainer to="/login">
                                <NavItem eventKey={1}>Login</NavItem>
                            </LinkContainer>
                        }

                        <LinkContainer to="/signip">
                            <NavItem eventKey={1}>Sigup</NavItem>
                        </LinkContainer>

                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        )
    }
}



export default Header;

