import React from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';


class Header extends React.Component {
    state = {
        data: null
    }
    componentDidMount() {
        const getdata = JSON.parse(localStorage.getItem("login"));
        try {
            if (getdata) {
                this.setState({ data: getdata });
            }
        } catch (e) { }
    }
    // componentDidUpdate(prevProps, prevState) {
    //     if (prevState.data.length !== this.state.data.length) {
    //         const stored = JSON.stringify(this.state.value);
    //         localStorage.setItem("goat", stored);
    //     }
    // }


    render() {
        return (
            <Navbar inverse collapseOnSelect>
                <Navbar.Header>
                    <Navbar.Brand>
                        <NavLink to="/" >Home</NavLink>
                    </Navbar.Brand>
                    <Navbar.Toggle />
                </Navbar.Header>
                <Navbar.Collapse>
                    <Nav>
                        <LinkContainer to="/shop">
                            <NavItem eventKey={1}>Shop</NavItem>
                        </LinkContainer>
                    </Nav>
                    <Nav pullRight>
                        {this.state.login ? <LinkContainer to="/addtocart">
                            <NavItem eventKey={1}>Add To cart</NavItem>
                        </LinkContainer>
                            :
                            <LinkContainer to="/login">
                                <NavItem eventKey={1}>Login</NavItem>
                            </LinkContainer>
                        }

                        <LinkContainer to="/signip">
                            <NavItem eventKey={1}>Sigup</NavItem>
                        </LinkContainer>

                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        )
    }
}



export default Header;

