import React from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';




class Header extends React.Component {
    state = {
        loginornot: false
    }
    Signout = () => { sessionStorage.removeItem("login") };

    componentDidMount() {
        Checkloginornot = () => {
            const login = sessionStorage.getItem("login");

            if (login) {
                this.setState({ loginornot: true })
            } else {
                this.setState({ loginornot: false })
            }
        }



    }


    render() {
        return (
            <Navbar inverse collapseOnSelect>
                <Navbar.Header>
                    <Navbar.Brand>
                        <NavLink to="/" >Home</NavLink>
                    </Navbar.Brand>
                    <Navbar.Toggle />
                </Navbar.Header>
                <Navbar.Collapse>
                    <Nav>
                        <LinkContainer to="/shop">
                            <NavItem eventKey={1}>Shop</NavItem>
                        </LinkContainer>
                    </Nav>
                    <Nav pullRight>
                        {this.state.loginornot ?
                            <LinkContainer to="/addtocart">
                                <NavItem eventKey={1}>Add To cart</NavItem>
                            </LinkContainer>
                            :
                            <LinkContainer to="/login">
                                <NavItem eventKey={1}>Login</NavItem>
                            </LinkContainer>
                        }

                        <LinkContainer to="/signip">
                            <NavItem eventKey={1}>Sigup</NavItem>
                        </LinkContainer>

                        <button className="btn btn-primary"
                            onClick={e => this.Signout}>
                            Sign Out
                        </button>

                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        )
    }

}

export default Header;
