import React from 'react';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';



class Header extends React.Component {
    state = {
        login: false,
        showSignout: false
    }
    componentDidMount() {

    }
    signout = () => {
        const removed = sessionStorage.removeItem("login");
        if (removed) {
            this.setState({ login: false })
        }
    }
    componentDidUpdate() {
        const data = sessionStorage.getItem("login");
        if (data) {
            this.setState({ login: !this.state.loginCheck, showSignout: !this.setState.showSignout })
        }
    }
    render() {
        return (

            <Navbar inverse collapseOnSelect>
                <Navbar.Header>
                    <Navbar.Brand>
                        <NavLink to="/" >Home</NavLink>
                    </Navbar.Brand>
                    <Navbar.Toggle />
                </Navbar.Header>
                <Navbar.Collapse>
                    <Nav>
                        <LinkContainer to="/shop">
                            <NavItem eventKey={1}>Shop</NavItem>
                        </LinkContainer>
                    </Nav>
                    <Nav pullRight>
                        {this.state.loginCheck ? <LinkContainer to="/addtocart">
                            <NavItem eventKey={1}>Add To cart</NavItem>
                        </LinkContainer> :
                            <LinkContainer to="/login">
                                <NavItem eventKey={1}>Login</NavItem>
                            </LinkContainer>
                        }

                        <LinkContainer to="/signip">
                            <NavItem eventKey={1}>Sigup</NavItem>
                        </LinkContainer>
                        {this.state.showSignout ?
                            <NavItem eventKey={2} href="#">
                                <button onClick={this.signout}>signOut</button>
                            </NavItem>
                            : <React.Fragment></React.Fragment>}
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        )
    }
}

export default Header;
